/// Compressed datastreams are represented as arrays of JOCTET.
/// These must be EXACTLY 8 bits wide, at least once they are written to
/// external storage.  Note that when using the stdio data source/destination
/// managers, this is also the data type passed to fread/fwrite.
typedef JOCTET = ffi.UnsignedChar;
typedef DartJOCTET = int;

enum J_COLOR_SPACE {
  /// error/unspecified
  JCS_UNKNOWN(0),

  /// monochrome
  JCS_GRAYSCALE(1),

  /// red/green/blue as specified by the RGB_RED,
  /// RGB_GREEN, RGB_BLUE, and RGB_PIXELSIZE macros
  JCS_RGB(2),

  /// Y/Cb/Cr (also known as YUV)
  JCS_YCbCr(3),

  /// C/M/Y/K
  JCS_CMYK(4),

  /// Y/Cb/Cr/K
  JCS_YCCK(5),

  /// red/green/blue
  JCS_EXT_RGB(6),

  /// red/green/blue/x
  JCS_EXT_RGBX(7),

  /// blue/green/red
  JCS_EXT_BGR(8),

  /// blue/green/red/x
  JCS_EXT_BGRX(9),

  /// x/blue/green/red
  JCS_EXT_XBGR(10),

  /// x/red/green/blue
  JCS_EXT_XRGB(11),

  /// red/green/blue/alpha
  JCS_EXT_RGBA(12),

  /// blue/green/red/alpha
  JCS_EXT_BGRA(13),

  /// alpha/blue/green/red
  JCS_EXT_ABGR(14),

  /// alpha/red/green/blue
  JCS_EXT_ARGB(15),

  /// 5-bit red/6-bit green/5-bit blue
  /// [decompression only]
  JCS_RGB565(16);

  final int value;
  const J_COLOR_SPACE(this.value);

  static J_COLOR_SPACE fromValue(int value) => switch (value) {
        0 => JCS_UNKNOWN,
        1 => JCS_GRAYSCALE,
        2 => JCS_RGB,
        3 => JCS_YCbCr,
        4 => JCS_CMYK,
        5 => JCS_YCCK,
        6 => JCS_EXT_RGB,
        7 => JCS_EXT_RGBX,
        8 => JCS_EXT_BGR,
        9 => JCS_EXT_BGRX,
        10 => JCS_EXT_XBGR,
        11 => JCS_EXT_XRGB,
        12 => JCS_EXT_RGBA,
        13 => JCS_EXT_BGRA,
        14 => JCS_EXT_ABGR,
        15 => JCS_EXT_ARGB,
        16 => JCS_RGB565,
        _ => throw ArgumentError("Unknown value for J_COLOR_SPACE: $value"),
      };
}

/// DCT/IDCT algorithm options.
enum J_DCT_METHOD {
  /// accurate integer method
  JDCT_ISLOW(0),

  /// less accurate integer method [legacy feature]
  JDCT_IFAST(1),

  /// floating-point method [legacy feature]
  JDCT_FLOAT(2);

  final int value;
  const J_DCT_METHOD(this.value);

  static J_DCT_METHOD fromValue(int value) => switch (value) {
        0 => JDCT_ISLOW,
        1 => JDCT_IFAST,
        2 => JDCT_FLOAT,
        _ => throw ArgumentError("Unknown value for J_DCT_METHOD: $value"),
      };
}

/// Dithering options for decompression.
enum J_DITHER_MODE {
  /// no dithering
  JDITHER_NONE(0),

  /// simple ordered dither
  JDITHER_ORDERED(1),

  /// Floyd-Steinberg error diffusion dither
  JDITHER_FS(2);

  final int value;
  const J_DITHER_MODE(this.value);

  static J_DITHER_MODE fromValue(int value) => switch (value) {
        0 => JDITHER_NONE,
        1 => JDITHER_ORDERED,
        2 => JDITHER_FS,
        _ => throw ArgumentError("Unknown value for J_DITHER_MODE: $value"),
      };
}

